import React, { useState, useEffect } from 'react';
import artImage from '../assets/art.jpg'; // Adjust the path based on your folder structure
import electronicsImage from '../assets/electronics.jpg';
import collectiblesImage from '../assets/collectibles.jpg';
import furnitureImage from '../assets/furniture.jpg';

const Carousel = ({ items }) => {
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const autoSlide = setInterval(() => {
            nextSlide();
        }, 3000); // Change slide every 3 seconds

        return () => clearInterval(autoSlide);
    }, [currentIndex]);

    const nextSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % items.length);
    };

    const prevSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + items.length) % items.length);
    };

    return (
        <div className="relative overflow-hidden">
            <div className="flex transition-transform duration-500" style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
                {items.map((item, index) => (
                    <div key={index} className="flex-shrink-0 w-full md:w-1/2 lg:w-1/3 xl:w-1/4 p-4">
                        <div className="bg-gray-200 rounded-lg shadow-md p-6 h-full flex flex-col items-center justify-center">
                            <img src={item.image} alt={item.title} className="w-full h-48 object-cover rounded-lg mb-4" />
                            <h3 className="text-lg font-semibold mb-2">{item.title}</h3>
                            <p className="text-gray-600 text-center">{item.description}</p>
                        </div>
                    </div>
                ))}
            </div>
            <button onClick={prevSlide} className="absolute top-1/2 left-0 transform -translate-y-1/2 p-2 bg-yellow-600 text-white rounded-r-lg shadow-lg hover:bg-yellow-700">
                &#10094;
            </button>
            <button onClick={nextSlide} className="absolute top-1/2 right-0 transform -translate-y-1/2 p-2 bg-yellow-600 text-white rounded-l-lg shadow-lg hover:bg-yellow-700">
                &#10095;
            </button>
        </div>
    );
};

// Example categories
const categories = [
    { title: 'Art', description: 'Beautiful artworks and sculptures.', image: artImage },
    { title: 'Electronics', description: 'Latest gadgets and devices.', image: electronicsImage },
    { title: 'Collectibles', description: 'Rare and valuable items.', image: collectiblesImage },
    { title: 'Furniture', description: 'Stylish and functional furniture.', image: furnitureImage },
];

export default Carousel;
