import React, { useState } from 'react';

const SearchBar = () => {
    const [searchTerm, setSearchTerm] = useState('');

    const handleChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(`Searching for: ${searchTerm}`);
    };

    return (
        <form onSubmit={handleSubmit} className="relative w-full max-w-xl mx-auto">
            <label htmlFor="search" className="sr-only">Search</label>
            <input
                id="search"
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleChange}
                className="w-full py-2 pl-10 pr-8 rounded-full bg-white text-blue-600 border border-gray-300 shadow-md focus:outline-none focus:border-blue-400 focus:ring-2 focus:ring-blue-200 focus:ring-opacity-50"
                aria-label="Search"
            />
            <span className="absolute inset-y-0 left-0 pl-3 flex items-center">
                <svg className="h-5 w-5 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-4.35-4.35m-3.65 1.35A7.5 7.5 0 1117 10.5a7.5 7.5 0 01-3.65 6.35z"></path>
                </svg>
            </span>
        </form>
    );
};

export default SearchBar;
