import React, { useState, useEffect } from 'react';

// Import images
import artImage from '../assets/art.jpg';
import electronicsImage from '../assets/electronics.jpg';
import collectiblesImage from '../assets/collectibles.jpg';
import furnitureImage from '../assets/furniture.jpg';

const Carousel = ({ items }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const autoSlide = setInterval(() => {
      nextSlide();
    }, 5000); // Change slide every 5 seconds

    return () => clearInterval(autoSlide);
  }, [items]); // Only re-run when items change

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % items.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + items.length) % items.length);
  };

  if (!items || items.length === 0) {
    return <div>No items to display.</div>;
  }

  return (
    <div className="relative overflow-hidden">
      <button onClick={prevSlide} aria-label="Previous Slide">Prev</button>
      <button onClick={nextSlide} aria-label="Next Slide">Next</button>
      <div
        className="flex transition-transform ease-in-out duration-1000"
        style={{ transform: `translateX(${currentIndex * -100}%)` }}
      >
        {items.map((item, index) => (
          <div key={index} className="flex-shrink-0 w-full">
            <img
              src={item.image}
              alt={item.title}
              className="w-full h-auto object-cover rounded-lg mb-4"
              style={{ maxHeight: '64vh', objectFit: 'cover', objectPosition: 'center' }} // Responsive height
            />
            <h3 className="text-lg font-semibold mb-2">{item.title}</h3>
            <p className="text-gray-600 text-center">{item.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

// Example categories remain unchanged

const App = () => {
  return (
    <div>
      <Carousel items={categories} />
    </div>
  );
};

export default App;
