import React, { useState, useEffect } from 'react';

// Import images
import artImage from '../assets/art.jpg';
import electronicsImage from '../assets/electronics.jpg';
import collectiblesImage from '../assets/collectibles.jpg';
import furnitureImage from '../assets/furniture.jpg';

const Carousel = ({ items }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const autoSlide = setInterval(() => {
      nextSlide();
    }, 5000); // Change slide every 5 seconds

    return () => clearInterval(autoSlide);
  }, [items]);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % items.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + items.length) % items.length);
  };

  if (!items || items.length === 0) {
    return <div>No items to display.</div>;
  }

  return (
    <div className="relative overflow-hidden">
      <div
        className="flex transition-transform duration-1000 ease-in-out"
        style={{ transform: `translateX(-${currentIndex * 100}%)` }}
      >
        {items.map((item, index) => (
          <div key={index} className="flex-shrink-0 w-full flex items-center justify-center p-4">
            <div className="w-full max-w-md rounded-lg shadow-lg overflow-hidden">
              <img
                src={item.image}
                alt={item.title}
                className="w-full h-64 object-cover"
                style={{ objectFit: 'cover' }}
              />
              <div className="p-4">
                <h3 className="text-xl font-semibold mb-2">{item.title}</h3>
                <p className="text-gray-700">{item.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Navigation buttons */}
      <button
        onClick={prevSlide}
        className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
      >
        &#10094;
      </button>
      <button
        onClick={nextSlide}
        className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
      >
        &#10095;
      </button>

      {/* Indicators */}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {items.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentIndex(index)}
            className={`w-3 h-3 rounded-full ${currentIndex === index ? 'bg-blue-500' : 'bg-gray-400'}`}
          />
        ))}
      </div>
    </div>
  );
};

// Example categories
const categories = [
  { title: 'Art', description: 'Beautiful artworks and sculptures.', image: artImage },
  { title: 'Electronics', description: 'Latest gadgets and devices.', image: electronicsImage },
  { title: 'Collectibles', description: 'Rare and valuable items.', image: collectiblesImage },
  { title: 'Furniture', description: 'Stylish and functional furniture.', image: furnitureImage },
  // Add more categories as needed
];

const App = () => {
  return (
    <div className="p-4">
      <Carousel items={categories} />
    </div>
  );
};

export default App;
